INSERT INTO RECIPE(RecipeID,RecipeName,RecipeDesc,AuthorID,PrepTime,CookTime) VALUES (default,'Paneer Bhurji recipe','Paneer bhurji is sometimes used as a stuffing for dosas to make paneer dosa, similar to the aloo-filled masala dosa! the most important tip for making fantastic paneer bhurji is to use fresh cottage cheese, and make the bhurji just before serving; otherwise it tends to get a little soggy.',2,'10-15 minutes','10-15 minutes');
