
 DROP DATABASE IF EXISTS ONLINE_RECIPE;
 CREATE DATABASE IF NOT EXISTS ONLINE_RECIPE;

USE ONLINE_RECIPE;

DROP TABLE IF EXISTS AUTHOR;
CREATE TABLE IF NOT EXISTS AUTHOR(
   AuthorID   INTEGER  NOT NULL PRIMARY KEY AUTO_INCREMENT
  ,AuthorName VARCHAR(45) NOT NULL UNIQUE
);


 


DROP TABLE IF EXISTS STEP;
CREATE TABLE IF NOT EXISTS STEP(
   StepID     INTEGER  NOT NULL PRIMARY KEY AUTO_INCREMENT
  ,RecipeID   INTEGER  NOT NULL
  ,StepNumber INTEGER  NOT NULL
  ,StepDesc   VARCHAR(500) NOT NULL
    ,FOREIGN KEY (RecipeID) REFERENCES RECIPE(RecipeID)
  ON UPDATE CASCADE ON DELETE RESTRICT
);
 set foreign_key_checks=0;


DROP TABLE IF EXISTS INGREDIENT;
CREATE TABLE IF NOT EXISTS INGREDIENT(
   IngredientID   INTEGER  NOT NULL PRIMARY KEY AUTO_INCREMENT
  ,IngredientName VARCHAR(45) NOT NULL
);




DROP TABLE IF EXISTS RECIPE_INGREDIENT;
CREATE TABLE IF NOT EXISTS RECIPE_INGREDIENT(
   RecipeID           INTEGER  NOT NULL
  ,IngredientID       INTEGER  NOT NULL
  ,IngredientAmt      INTEGER 
  ,AmtUnits           VARCHAR(45)
  ,RecipeIngredientId INTEGER  NOT NULL PRIMARY KEY AUTO_INCREMENT
 ,FOREIGN KEY (RecipeID) REFERENCES RECIPE(RecipeID)
  ON UPDATE CASCADE ON DELETE RESTRICT
  ,FOREIGN KEY (IngredientID) REFERENCES INGREDIENT(IngredientID)
  ON UPDATE CASCADE ON DELETE RESTRICT
);
 set foreign_key_checks=0;


DROP TABLE IF EXISTS RECIPE;
CREATE TABLE IF NOT EXISTS RECIPE(
   RecipeID   INTEGER  NOT NULL PRIMARY KEY AUTO_INCREMENT
  ,RecipeName VARCHAR(60) NOT NULL
  ,RecipeDesc VARCHAR(500)
  ,AuthorID   INTEGER  NOT NULL
  ,PrepTime   VARCHAR(45) NOT NULL
  ,CookTime   VARCHAR(45)
 ,FOREIGN KEY (AuthorID) REFERENCES AUTHOR(AuthorID)
  ON UPDATE CASCADE ON DELETE RESTRICT
  );
 set foreign_key_checks=0;


DROP TABLE IF EXISTS SignUp;
CREATE TABLE IF NOT EXISTS SignUp(
   userName  VARCHAR(255) NOT NULL PRIMARY KEY
  ,Password  VARCHAR(255) NOT NULL
  ,firstName VARCHAR(15) NOT NULL
  ,lastName  VARCHAR(15) NOT NULL
  ,Address   VARCHAR(45) NOT NULL
);








